import React from 'react';
import { Container, Row, Col, Tab, Nav } from 'react-bootstrap';
import 'animate.css';
import './Experience.css'; // Create a corresponding CSS file for styling
import TrackVisibility from 'react-on-screen';

export const Experience = () => {
  const workExperiences = [
    {
      title: 'Software Engineer Intern',
      company: 'ABC Tech',
      date: 'May 2022 - August 2022',
      description: 'Developed and maintained web applications, collaborated with cross-functional teams, and contributed to project delivery.'
    },
    {
      title: 'IT Support Specialist',
      company: 'XYZ Corporation',
      date: 'January 2021 - April 2021',
      description: 'Provided technical support to employees, resolved IT issues, and managed software and hardware deployments.'
    },
    // Add more work experiences here
  ];

  return (
    <section className="experience" id="experience">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? 'animate__animated animate__fadeIn' : ''}>
                  <h2>Work Experience</h2>
                  <Tab.Container id="experience-tabs" defaultActiveKey="first">
                    <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
                      {workExperiences.map((experience, index) => (
                        <Nav.Item key={index}>
                          <Nav.Link eventKey={`experience-${index + 1}`}>{experience.company}</Nav.Link>
                        </Nav.Item>
                      ))}
                    </Nav>
                    <Tab.Content id="slideInUp" className={isVisible ? 'animate__animated animate__slideInUp' : ''}>
                      {workExperiences.map((experience, index) => (
                        <Tab.Pane key={index} eventKey={`experience-${index + 1}`}>
                          <h3>{experience.title}</h3>
                          <p>{experience.date}</p>
                          <p>{experience.description}</p>
                        </Tab.Pane>
                      ))}
                    </Tab.Content>
                  </Tab.Container>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      {/* Add any background image or design elements here */}
    </section>
  );
};
