import React, { useState } from "react";
import { Container, Row, Col, Tab, Nav, TabPane } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import projImg1 from "../assets/img/project-img1.png";
import projImg2 from "../assets/img/project-img2.png";
import projImg3 from "../assets/img/project-img3.png";
import colorSharp2 from "../assets/img/color-sharp2.png";
import videoFile from "../assets/video/climate.mp4";
import "animate.css";
import TrackVisibility from "react-on-screen";

import "./Projects.css";

export const Projects = () => {
  const [activeTab, setActiveTab] = useState("first");

  const handleTabSelect = (eventKey) => {
    setActiveTab(eventKey);
  };

  const projects = [
    {
      title: "Project 1",
      description: "Design & Development",
      content: (
        <video width="100%" controls>
          <source src={videoFile} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      ),
    },
    {
      title: "Project 2",
      description: "Design & Development",
      imgUrl: projImg2,
    },
    {
      title: "Project 3",
      description: "Design & Development",
      imgUrl: projImg3,
    },
  ];

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <h2>Projects</h2>
                  <p>
                    Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been
                    the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of
                    type and scrambled it to make a type specimen book.
                  </p>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <Tab.Container id="projects-tabs" activeKey={activeTab} onSelect={handleTabSelect}>
        <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
          <Nav.Item>
            <Nav.Link eventKey="first">Tab 1</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="second">Tab 2</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="third">Tab 3</Nav.Link>
          </Nav.Item>
        </Nav>
        <Tab.Content id="slideInUp">
          <Tab.Pane eventKey="first">
            <Row>
              {projects.map((project, index) => (
                <Col size={12} sm={6} md={4} key={index}>
                  <ProjectCard {...project} />
                </Col>
              ))}
            </Row>
          </Tab.Pane>
          <Tab.Pane eventKey="second">
            <p>Content for the second tab goes here.</p>
          </Tab.Pane>
          <Tab.Pane eventKey="third">
            <p>Content for the third tab goes here.</p>
          </Tab.Pane>
        </Tab.Content>
      </Tab.Container>
      <img className="background-image-right" src={colorSharp2} alt="Background" />
    </section>
  );
};
