import { Container, Row, Col, Tab, Nav } from "react-bootstrap";
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import "./Experience.css";
import TrackVisibility from 'react-on-screen';

export const Experience = () => {
  return (
    <section className="skill" id="Experience">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <h2 className={isVisible ? "experience-visible" : ""}>Experience</h2>
                  <Tab.Container id="Experience-tabs" defaultActiveKey="first">
                    <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
                      {/* Add similar class handling for Nav.Item and Nav.Link */}
                      <Nav.Item className={isVisible ? "experience-visible" : ""}>
                        <Nav.Link eventKey="first">Internships</Nav.Link>
                      </Nav.Item>
                      {/* Other Nav.Item and Nav.Link elements */}
                    </Nav>
                    <Tab.Content id="slideInUp" className={isVisible ? "animate__animated animate__slideInUp" : ""}>
                      <Tab.Pane eventKey="first">
                        <div className={`experience-item ${isVisible ? "experience-visible" : ""}`}>
                          {/* Content for first internship */}
                        </div>
                        <hr className={`experience-divider ${isVisible ? "experience-visible" : ""}`} />
                        <div className={`experience-item ${isVisible ? "experience-visible" : ""}`}>
                          {/* Content for second internship */}
                        </div>
                        {/* Other content elements */}
                      </Tab.Pane>
                      {/* Other Tab.Pane sections */}
                    </Tab.Content>
                  </Tab.Container>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="Background" />
    </section>
  );
};
