import React from "react";
import { Card, Col } from "react-bootstrap";
import TrackVisibility from "react-on-screen";


export const ProjectCard = ({ title, description, imgUrl, animate }) => {
  return (
    <Col sm={6} md={4} lg={3}>
      <TrackVisibility>
        {({ isVisible }) => (
          <Card
            className={`project-card ${isVisible && animate ? "animate__animated animate__fadeInUp" : ""}`}
          >
            <Card.Img variant="top" src={imgUrl} alt={title} />
            <Card.Body>
              <Card.Title>{title}</Card.Title>
              {description && <Card.Text>{description}</Card.Text>}
            </Card.Body>
          </Card>
        )}
      </TrackVisibility>
    </Col>
  );
};
