import React from 'react';
import { Container, Row, Col, Tab } from 'react-bootstrap';
import colorSharp2 from '../assets/img/color-sharp2.png';
import 'animate.css';
import './Experience.css';
import TrackVisibility from 'react-on-screen';

export const Experience = () => {
  const workExperience = [
    {
      title: 'Software Engineer Intern',
      company: 'ABC Tech',
      date: 'May 2022 - August 2022',
      description:
        'Developed and maintained web applications, collaborated with cross-functional teams, and contributed to project delivery.',
    },
    {
      title: 'IT Support Specialist',
      company: 'XYZ Corporation',
      date: 'January 2021 - April 2021',
      description:
        'Provided technical support to employees, resolved IT issues, and managed software and hardware deployments.',
    },
    // Add more work experiences here
  ];

  return (
    <section className="experience" id="experience">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? 'animate__animated animate__fadeIn' : ''}>
                  <h2>Work Experience</h2>
                  <Tab.Container id="experience-tabs" defaultActiveKey="first">
                    <Tab.Content id="slideInUp" className={isVisible ? 'animate__animated animate__slideInUp' : ''}>
                      {workExperience.map((experience, index) => (
                        <Tab.Pane key={index} eventKey={`experience-${index + 1}`}>
                          <h3>{experience.title}</h3>
                          <p>{experience.company}</p>
                          <p>{experience.date}</p>
                          <p>{experience.description}</p>
                        </Tab.Pane>
                      ))}
                    </Tab.Content>
                  </Tab.Container>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="Background Image" />
    </section>
  );
};
